mapper.select:
SELECT DISTINCT c.id, c.name, (SELECT ROUND(AVG(score)) FROM user_skill_evaluation use WHERE use.competency_id = c.id AND use.user_id IN ( SELECT DISTINCT cu.user_id FROM core_users cu WHERE cu.discipline_id = ct.discipline_id AND ((${@com.iberdrola.core.profiles.constants.ProfileConstants@RESPONSABLE_ORGANIZACION} <> #{id, typeHandler = com.iberdrola.cappfdmd.common.handler.UserProfileIdTypeHandler}::numeric) OR (${@com.iberdrola.core.profiles.constants.ProfileConstants@RESPONSABLE_ORGANIZACION} = #{id, typeHandler = com.iberdrola.cappfdmd.common.handler.UserProfileIdTypeHandler}::numeric AND cu.organization_id = #{id, typeHandler = com.iberdrola.cappfdmd.common.handler.UserOrganizationIdTypeHandler}::numeric)) ) ) AS average_user_score, (SELECT ROUND(AVG(expected)) FROM competencies_disciplines_skills cds2 WHERE cds2.competency_id = c.id AND expected IS NOT NULL
AND cds2.discipline_id = #{discipline}
) AS average_score FROM competencies c JOIN competencies_disciplines ct ON ct.competency_id = c.id LEFT JOIN competencies_disciplines_skills cds ON cds.competency_id = c.id
ct.discipline_id = #{discipline}
ct.discipline_id IN ()
AND cds.selected = #{selected}
AND NAME #{filter}
